 month      int4range NOT NULL,
 week       int4range NOT NULL,
 week_day   int4range NOT NULL,
 CONSTRAINT PK_4 PRIMARY KEY ( ship_date, order_date )
);

-- ************************************** Geography
CREATE TABLE Geography
(
