 product_name varchar(250) NOT NULL,
 CONSTRAINT PK_2 PRIMARY KEY ( product_id )
);

-- ************************************** Shipping
CREATE TABLE Shipping
(
 ship_id   int NOT NULL,
 ship_mode varchar(50) NOT NULL,
 CONSTRAINT PK_1 PRIMARY KEY ( ship_id )
);


-- ************************************** Sales
CREATE TABLE Sales
(
 row_id      NOT NULL,
 order_id    NOT NULL,
 sales       NOT NULL,
